import React from 'react';
import Product from './Products';
import './ProductList.css';

const products = [
  // An array of product data, each containing a title, description, and image URL.
  // Example: { title: 'Product 1', description: 'Description of Product 1', imageUrl: 'url_to_image_1' }
  { title: 'Pan40', description: 'Acidity', imageUrl: 'https://www.practostatic.com/practopedia-images/v3/res-750/pan-40mg-tablet-15-s_e0c8cd8e-1788-4c84-8491-99aa23ab675a.JPG' },
  { title: 'Crocin', description: 'Fever', imageUrl: 'https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcSgL5O6HIWo1i9Aadxp_l6hu5X2G1qaY3TfStQFvG10Tlpj0cGx' },
  { title: 'Paracip500', description: 'Flu', imageUrl: 'https://5.imimg.com/data5/SELLER/Default/2021/6/AC/ZD/LY/122822982/85513-1-1000.jpg'},
   { title: 'Metformin', description: 'Diabetes', imageUrl: 'data:image/jpeg;base64,' },
  { title: 'Cephalosporin', description: 'Typhoid', imageUrl: 'data:image/jpeg;base64,' },
  { title: 'Refresh Eye drop', description: 'Eyes', imageUrl: 'data:image/jpeg;base64,' },
  { title: 'Nicotex', description: 'Nicotine', imageUrl: 'data:image/jpeg;base64,' },
  { title: 'Betadine', description: 'Cuts and Wounds', imageUrl: 'data:image/jpeg;base64,'}];

function ProductList({ searchTerm }) {
  const filteredProducts = products.filter((product) =>
  product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
  product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="product-list">
      {filteredProducts.map((product, index) => (
        <Product key={index} product={product} />
      ))}
    </div>
  );
}

export default ProductList;